def get_access_token() -> str:
    auth = ClientSecretCredential(
        authority=AUTHORITY_URL,
        tenant_id=PBI_TENANT_ID,
        client_id=PBI_APP_ID,
        client_secret=PBI_CLIENT_SECRET,
    )
    access_token = auth.get_token(PBI_API)
